module mac_32(
    input wire clk,
    input wire reset,
    input wire [31:0] A,
    input wire [31:0] B,
    output reg [63:0] accumulator
);
always @(posedge clk) begin
    if (reset) begin
        accumulator <= 0;
    end else begin
        accumulator <= accumulator + A * B;
    end
