completion:
parameter A=0, B=1, C=2, D=3;
	reg [1:0] state, next_state;

	always @(*) begin
		state <= next_state;
	end

	always @(posedge clk, posedge areset) begin
		if(areset)
			next_state <= A;
		else
			case(state)
				A: begin
					if(in)
						next_state <= B;
					else
						next_state <= A;
				end
				B: begin
					if(in)
						next_state <= B;
					else
						next_state <= C;
				end
				C: begin
					if(in)
						next_state <= D;
					else
						next_state <= A;
				end
				D: begin
					if(in)
						next_state <= B;
					else
						next_state <= C;
				end
				default: next_state <= A;
			endcase
	end

	assign out = (state == D)? 1:0;

endmodule
